$(".icon-menu").click(function (e) { $(this).toggleClass("active"), $(".menu__body").toggleClass("active"), $("body").toggleClass("lock") }); const sliderCards = document.querySelectorAll(".sliders__card"), sliderTrack = document.querySelector(".slider__track"); let width, count = 0; function initial() { console.log("resize"), width = document.querySelector(".slider").offsetWidth, sliderTrack.style.width = width * sliderCards.length + "px", sliderCards.forEach(e => { e.style.width = width / 3 + "px", e.style.height = "auto", rollSlider() }) } function rollSlider() { sliderTrack.style.transform = "translate(-" + count * width / 3 + "px)" } window.addEventListener("resize", initial), initial(), document.querySelector(".button__slider-prev").addEventListener("click", function () { --count < 0 && (count = sliderCards.length - 1), rollSlider() }), document.querySelector(".button__slider-next").addEventListener("click", function () { ++count >= sliderCards.length && (count = 0), rollSlider() });